const route = require('koa-route');

const io = require('./io');
const posts = require('./posts');

function router(app) {
    app.use(route.post('/bts/api/', io.get));
    app.use(route.post('/bts/api/post/', posts.post));
    app.use(route.get('/bts/api/post/', posts.get));
};

module.exports = {
    use: router
};
