const JSCK = require('jsck');
const fs = require('fs');
const path = require('path');

const schema = JSON.parse(fs.readFileSync(path.join(__dirname,'schema.json'), 'utf-8'));
const jsck = new JSCK.draft4(schema);

console.log(
    jsck.validate({
        "page": "index",
        "fields": [{
            "anchor": "main title",
            "content": "Hello"
        }, {
            "anchor": "main content",
            "content": "Hello <em>world</em>"
        }]
    })
);