module.exports = function(req, res) {
    if (req.headers.accept === 'application/json' && req.headers['content-type'] === 'application/json; charset=UTF-8') {
        var validated = jsck.validate(req.body);
        if (validated.valid) {
            res.json(JSON.stringify(validated));
        }
    }
};
