const path = require('path');

const Configuration = function() {
    
    this.root = path.join(__dirname, '../..'); // /node_modules/hibiku
    this.userRoot = path.join(this.root, '../..'); // /

    this.engine = {

        "extension": ".hb",
        
        "views": [
            path.join(this.userRoot, "visage"),
            path.join(this.root, "bts/pages")
        ]

    };

    this.db = {
     
        "host": "localhost",
        
        "name": "hibiku"
        
    };
    
    this.options = {
        
        "locale": "en-gb",
        "frontPage": "index"
        
    };

};

module.exports = new Configuration;