const express = require('express');
const app = express();
const bodyParser = require('body-parser');
const path = require('path');

const config = require('./config');
const utils = require('./utils');

const engine = require('./engine/');
engine.use(app);

app.disable('x-powered-by');

var files = [];
config.engine.views.forEach(function(directory) {
    utils.walk(directory, function(f, s) {
        if (!(f.indexOf(config.root) > -1))
            files.push(f.replace(config.user.root, '').replace(new RegExp(config.engine.extension + "$"), ''));
    });
});

var template = {
    'index': {
        'main title': 'Yo',
        'main content area': 'peace'
    },
    'hibiku-editor': {
        files: files
    }
};

app.get('/', function(req, res, next) {
    return res.render(config.user.options.frontPage, template[config.user.options.frontPage]);
    next();
});

const api = require('./api');
api.use(app);

var routes = [];
config.engine.views.forEach(function(directory) {
    utils.walk(directory, function(f, s) {
        if (f.indexOf(config.root) > -1)
            routes.push(f.replace(config.root, ''));
        else
            routes.push(f.replace(directory, ''));
    });
});

routes.forEach(function(route) {
    
    route = route.replace(config.engine.extension, '');
    var hook = path.basename(route);
    if (route.indexOf('/bts/') === 0) {
        route = route.replace(/pages\/(hibiku-)?/, '');
    } else {
        var parts = route.split(path.sep);
        if (parts.length > 2)
            hook = parts[parts.length-2]+path.sep+parts[parts.length-1]; // for folders within views director(y|ies), make the route folder/filename instead of just filename
    }
    
    app.get(route, function(req, res, next) {
        return res.render(hook, template[hook]);
        next();
    });
    
});

app.use(express.static(config.root));
app.use(express.static(config.user.root));

app.use(function(req, res) {
    res.status(404).render('404', { url: req.url });
});


module.exports = {
    listen: function(port, ip) {
        console.log('\n  hibiku now listening to', ip+':'+port, '\n');
        app.listen(port, ip);
    },
    app: app
}
